--STATUS

CREATE TABLE public."Status" (
	"id" int4 NOT NULL GENERATED BY DEFAULT AS IDENTITY,	
	"name" varchar(50) NOT NULL,
	CONSTRAINT "PK_Status" PRIMARY KEY ("id")
);

